<script type="text/javascript" src="/js/transition.min.js"></script>
<script type="text/javascript" src="js/jquery.qtip.min.js"></script>
<script type="text/javascript" src="js/jquery.responsiveImageMaps.min.js"></script>
<script type="text/javascript">
    $(document).ready(function () {
        $('img[usemap]').rwdImageMaps();

        $('area[alt]').qtip({
            content: {
                title: function(api) { return $(this).attr('alt'); },
                text: function(api) { return $('li[name="'+$(this).attr('alt')+'"]').children().clone(); }
            },
            position: { my: 'left center', at: 'center' },
            hide: { delay: 100, fixed: true },
            style:   { classes: 	'map-tooltip' }
        });

        $('#map-state-select').change(function () {
            var state = $(this).val();
            $('#distributor-list li').each(function () {
                $(this).hide();
            })
            $("#distributor-list li[data-states*='" + state + "']").each(function () {
                $(this).show();
            })
        });

    });
</script>
